import React from 'react';

const BurnoutWarnings = ({ warnings }) => {
  const getRiskColor = (riskLevel) => {
    switch (riskLevel.toLowerCase()) {
      case 'critical': return 'bg-red-100 text-red-800 border-red-200';
      case 'high': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <h3 className="text-lg font-semibold text-gray-700 mb-4">Burnout Risk Alerts</h3>

      {warnings.length === 0 ? (
        <div className="text-center py-8">
          <div className="text-green-500 text-4xl mb-2">✅</div>
          <p className="text-gray-500">No burnout risks detected</p>
        </div>
      ) : (
        <div className="space-y-4">
          {warnings.map((warning, index) => (
            <div key={index} className={`p-4 rounded-lg border ${getRiskColor(warning.risk_level)}`}>
              <div className="flex items-center justify-between mb-2">
                <span className="font-semibold capitalize">{warning.risk_level} Risk</span>
                <span className="text-xs opacity-75">
                  {new Date(warning.timestamp).toLocaleDateString()}
                </span>
              </div>

              <div className="mb-3">
                <h4 className="font-medium mb-1">Indicators:</h4>
                <ul className="text-sm space-y-1">
                  {warning.indicators.map((indicator, idx) => (
                    <li key={idx} className="flex items-start">
                      <span className="mr-2">•</span>
                      <span>{indicator}</span>
                    </li>
                  ))}
                </ul>
              </div>

              <div>
                <h4 className="font-medium mb-1">Recommendation:</h4>
                <p className="text-sm">{warning.recommendation}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BurnoutWarnings;
