import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SentimentChart from './SentimentChart';
import ChannelList from './ChannelList';
import WeeklyTrends from './WeeklyTrends';
import BurnoutWarnings from './BurnoutWarnings';

const Dashboard = () => {
  const [channels, setChannels] = useState([]);
  const [weeklyTrends, setWeeklyTrends] = useState(null);
  const [burnoutWarnings, setBurnoutWarnings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const [channelsRes, trendsRes, warningsRes] = await Promise.all([
        axios.get('/api/engagement/channels'),
        axios.get('/api/engagement/trends/weekly'),
        axios.get('/api/engagement/burnout-warnings')
      ]);

      setChannels(channelsRes.data);
      setWeeklyTrends(trendsRes.data);
      setBurnoutWarnings(warningsRes.data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">
          Employee Engagement Pulse
        </h1>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700 mb-2">Active Channels</h3>
            <p className="text-3xl font-bold text-blue-600">{channels.length}</p>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700 mb-2">Total Messages</h3>
            <p className="text-3xl font-bold text-green-600">{weeklyTrends?.total_messages || 0}</p>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700 mb-2">Burnout Risk</h3>
            <p className={`text-3xl font-bold ${
              weeklyTrends?.burnout_risk === 'high' ? 'text-red-600' :
              weeklyTrends?.burnout_risk === 'medium' ? 'text-yellow-600' : 'text-green-600'
            }`}>
              {weeklyTrends?.burnout_risk?.toUpperCase() || 'LOW'}
            </p>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <SentimentChart weeklyTrends={weeklyTrends} />
          <WeeklyTrends data={weeklyTrends} />
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <ChannelList channels={channels} />
          <BurnoutWarnings warnings={burnoutWarnings} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
